from decimal import Decimal

pi = Decimal('3.')
a, b, c = map(Decimal, input().split())
start, end = Decimal('0'), Decimal(str(10**6))
facts = [1]*(2*250+2)
for i in range(1, 2*250+2):
  facts[i] = facts[i-1]*i

def pow(a, n):
  if n == 1:
    return a
  x = pow(a, n//2)
  if n%2 == 0:
    return x*x
  else:
    return x*x*a


def sin(x):
  x %= pi*2
  res = Decimal('0')
  for n in range(251):
    if n % 2 == 0:
      res += Decimal(str(pow(x, 2*n+1) / facts[2*n+1]))
    else:
      res -= Decimal(str(pow(x, 2*n+1) / facts[2*n+1]))
    
  return res

while abs(end-start) >= 1.0e-22:
  mid = Decimal(str((start + end) / 2))
  
  val = Decimal(str(a*mid + b*sin(mid)))
  
  if val >= c:
    end = mid
  else:
    start = mid

print(round(mid, 6))